import React from "react";
class App extends React.Component{
    constructor(){
        super();
        this.state={
            name:"welcome",
            value:"All"
        }
    }
    componentWillMount(){
        alert("wea are learning Component life cycle")
    }
    render(){
        return(
            <div>
                <p>{this.state.name} {this.state.value}</p>
                
<button onClick={this.change}>Click</button>
<p id="para"></p>
            </div>
        )
    }
    change=()=>{
        this.setState({
            name:"Good morninng"
        })
    }
    componentDidMount(){
 setTimeout(()=>{
    this.setState({
        name:"Thank You"
    })
 },2000)
        
    }
    componentWillUpdate(){
        alert("we need to update the state values?")
    }
    componentDidUpdate(){
        document.getElementById("para").innerHTML="updates state is: "+this.state.name

    }

}

export default App;
