// states --> pass the information from inside the component
//declared --> {}
//Mutable -->update the vslues
//two types 
//1.contructor method 
//is a function it is used to define instance of objects
//2.without constructor
import React from "react";
class App extends React.Component{
    constructor(){
        super();
        this.state={
           value:0
        }
    }
   change=()=>{
  this.setState({
    value:this.state.value + 1
  })
    }
    change1=()=>{
        this.setState({
          value:this.state.value - 1
        })
          }
    render(){
        return(
            <div>
                <h2>constructor Method</h2>
                <p>{this.state.value}</p>
               
                <button onClick={this.change}>Inc</button>
                <button onClick={this.change1}>dec</button>

            </div>
        )
    }
}

export default App;
