import React from "react";

class App extends React.Component{
    constructor(props){
        super(props);
        this.state={
            fname:"",
            lname:"",
            email:"",
            password:"",
            cpassword:""
        }
    }
handleFname=(a)=>{
    this.setState({
        fname:a.target.value
    })
}

handleLname=(a)=>{
    this.setState({
        lname:a.target.value
    })
}
handleEmail=(a)=>{
    this.setState({
        email:a.target.value
    })
}
handlePassword=(a)=>{
    this.setState({
        password:a.target.value
    })
}
handleCpassword=(a)=>{
    this.setState({
        cpassword:a.target.value
    })
}

handleSubmit=()=>{
    if(this.state.fname.length<5){
        alert("character should be > 5 ")
    }
    else if(this.state.password !== this.state.cpassword){
        alert("password does not match..!")
    }
    else{
        alert("Form succefully submited "+this.state.fname +" "+this.state.lname+" "+this.state.email)
    }
    
}

    render(){
        return(
            <div>
                <h2>Form validation</h2>
                <form onSubmit={this.handleSubmit}>
                    <label>First Name:</label>
                    <input value={this.state.fname} required type="text" placeholder="Enter Your Name" onChange={this.handleFname}></input>
                    <br/>< br/>
                    <label>Last Name:</label>
                    <input value={this.state.lname} required type="text" placeholder="Enter your last name" onChange={this.handleLname}/>
                    <br/><br/>
                    <label>Email:</label>
                    <input value={this.state.email} required type="email" placeholder="Enter your Email" onChange={this.handleEmail}/>
                    <br/><br/>
                    <label>Password:</label>
                    <input value={this.state.password} required type="password" placeholder="Enter your password" onChange={this.handlePassword}/>
                    <br/><br/>
                    <label>Confirm Password:</label>
                    <input value={this.state.cpassword} required type="password" placeholder="Enter your Confirm password" onChange={this.handleCpassword}/>
                    <br/><br/>
                    <input type="submit" value="Register" />

                </form>

            </div>
        )
    }
}

export default App;
