    const sortData = async (e) => {
        e.preventDefault()
        let value = e.target.value
        setSort(value)
        return await axios.get(`http://localhost:3007/users?_sort=${value}&_order=asc`)
            .then((res) => {
                setData(res.data)
            })
            .catch((err) => {
                console.log(err)
            })

    }




    return (
        <div className="container">


           

            <div className="">
                <div className="card-title">
                    <h3>Users List</h3>
                    {/* <form onSubmit={handleSubmit}>
                        <div className="mb-3">
