// useState() ->state,setState

import { useState } from "react";

function App(){
    const [loading,setLoading]=useState("helo")

    const change=()=>{
        setLoading("good morning")
    }

    return(
        <div>
            <h3>useState</h3>
            <p>helo this is my paragrha</p>
            <p>{loading}</p>

            <button onClick={change}>Click</button>

        </div>
    )
}
export default App;
