// states --> pass the information from inside the component
//declared --> {}
//Mutable -->update the vslues
//two types 
//1.contructor method 
//is a function it is used to define instance of objects
//2.without constructor
import React from "react";
class App extends React.Component{
    constructor(props){
        super(props);
        this.state={
            name:"Raghav",
            city:"Banglore"
        }
    }
   change=()=>{
    this.setState({
    name:"Vijay",
    city:"Tamil nadu"
})
    }
    render(){
        return(
            <div>
                <h2>constructor Method</h2>
                <p>{this.state.name}</p>
                <p>{this.state.city}</p>
                <button onClick={this.change}>Change</button>

            </div>
        )
    }
}

export default App;
