    const firebaseConfig = {
      apiKey: "AIzaSyDSMBO69sXkoJQjQsVpL3klLDIG5uPtX3A",
      authDomain: "auth-3ff16.firebaseapp.com",
      projectId: "auth-3ff16",
      storageBucket: "auth-3ff16.appspot.com",
