import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

function EmpList() {
    const [data, setData] = useState(null)
    const [value, setValue] = useState("")
    const navigate = useNavigate()

    useEffect(() => {
        loadData(0, 1)
    }, [])

    const loadData = () => {
        fetch(" http://localhost:3006/users")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                console.log(resp)
                setData(resp)
            })
    }
    // delete Functonality
    const onDelete = (id) => {
        if (window.confirm("Do you want delete?")) {
            fetch("http://localhost:3006/users/" + id, {
                method: "DELETE"
            })
                .then(() => {
                    alert("Deleted successfully..!!")
                    window.location.reload()
                })
        }
    }

    // search functionality
    const SearchData = (e) => {
        console.log(e.target.value)
        setValue(e.target.value)
    }
    const handleSubmit = async (e) => {
        e.preventDefault()
        return await axios.get(`http://localhost:3006/users?q=${value}`)
            .then((res) => {
                setData(res.data)
                setValue("")
            })
            .catch((err) => {
                console.log(err)
            })
    }
    // reset functinality
    const reset=(e)=>{
        e.preventDefault()
        fetch("http://localhost:3006/users")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                window.location.reload()
                setData(resp)
            })
    }


    //   Edit redirect
    const onEdit = (id) => {
        navigate("/user/" + id)
    }



    return (
        <div className="container">
            <nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">Navbar</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Link</a>
        </li>
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Dropdown
          </a>
          <ul class="dropdown-menu">
            <li><a class="dropdown-item" href="#">Action</a></li>
            <li><a class="dropdown-item" href="#">Another action</a></li>
            <li><hr class="dropdown-divider"/></li>
            <li><a class="dropdown-item" href="#">Something else here</a></li>
          </ul>
        </li>
        <li class="nav-item">
          <a class="nav-link disabled">Disabled</a>
        </li>
      </ul>
      <form class="d-flex" role="search">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button class="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>
            <div className="">
                <div className="card-title">
                    <h3>Users List</h3>

                    {/* <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label className="form-label">Search</label>
                            <input type="text" value={value} onChange={SearchData} className="form-control" />
                        </div>
                        <button type="submit" class="btn btn-primary">Search</button>
                        {/* <button onClick={reset} class="btn btn-primary">Reset</button> 
                    </form> */}

                </div>
                <div className="card-body">
                  <div className="row">
                    <div className="col-sm-2">
                    <Link to="/form" className="btn btn-success">
                        Add New User(+)
                    </Link>
                    </div>
                    <div className="col-sm-3">
                    <input type="text" value={value} placeholder="Filter Records..." onChange={SearchData} className="form-control" />

                    </div>
                    <div className="col-sm-1">
                    <button  onClick={handleSubmit} class="btn btn-primary">Search</button>
                    </div>
                    <div className="col-sm-1">
                    <button  onClick={reset} class="btn btn-">Reset</button>
                    </div>
                  </div>

                    {/* <select  style={{ float: "right", width: "150px" }} value={sort} onChange={sortData} class="form-select" aria-label="Default select example">
                        
                            <option selected>Sort By Name</option>
                            {options.map((item) => (
                                <option>{item}</option>
                            ))}
                        </select> */}


<br/>
                    <table className="table table-bordred">
                        <thead className="bg-dark text-white">
                            <tr>
                                <th>ID</th>
                                <th>Title</th>
                                <th>Description</th>
                                <th>Due Date</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data && data.map((item) => (
                                <tr key={item.id}>
                                    <td>{item.id}</td>
                                    <td>{item.title}</td>
                                    <td>{item.description}</td>
                                    <td>{item.date}</td>
                                    <td>
                                        <a onClick={() => { onDelete(item.id) }} className="btn btn-danger">Delete</a>
                                        <a onClick={() => { onEdit(item.id) }} className="btn btn-primary" style={{ marginLeft: "10px" }}>Edit</a>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

        </div>
    )
}

export default EmpList;
