// store,--->redux[createstore,applyMiddleware]
  //--->thunk -->redux-thunk
//  reducer,action
import { connect} from "react-redux"
import { inc } from "./action";
import { dec } from "./action";
function App({variable,inc,dec}){
  return(
    <div>
      <center>
      <h3>React-Redux</h3>
      <p>{variable}</p>
      <button onClick={inc}>Inc</button>
      <button onClick={dec}>Dec</button>
      </center>
    </div>
  )
  
}
const mapStateToProps=state=>({
  variable:state

})

export default connect(mapStateToProps,{inc,dec}) (App);
