import { initializeApp } from "firebase/app";
import { getAuth, createUserWithEmailAndPassword, signOut, signInWithEmailAndPassword } from "firebase/auth"
import { useState } from "react";

function Register() {
    const [fname,setFname]=useState("")
    const [email,setEmail]=useState("")
    // const []

    const firebaseConfig = {
        apiKey: "AIzaSyBesU-jmSlCe5SqWc12uylr1zu3agv5osY",
        authDomain: "signup-5f3f5.firebaseapp.com",
        projectId: "signup-5f3f5",
        storageBucket: "signup-5f3f5.appspot.com",
        messagingSenderId: "549475749271",
        appId: "1:549475749271:web:e1054654c3a500688dc8d2",
        measurementId: "G-Y1JFBEQPHZ"
    };

    const app = initializeApp(firebaseConfig);
    const auth = getAuth()  // email passowrd



    // var fullname = document.getElementById("fullname")
    // var email = document.getElementById('email')
    // var mobile = document.getElementById("mobile")
    // var pswd = document.getElementById("password")
    // var cpwsd = document.getElementById("cpassword")

    const signup = (e) => {
        e.preventDefault()
        if (fullname.value == "" || email.value == "" || mobile.value == "" || pswd.value == "" || cpwsd.value == "") {
            alert("All fields are manadatory..!")
        }
        if (pswd.value != cpwsd.value) {
            alert("password does not match...!!!!")
        }
    
        let obj = {
            email: email.value,
            password: pswd.value
        }
        createUserWithEmailAndPassword(auth, obj.email, obj.password)
            .then(() => {
                alert("successfully registred")

            })
            .catch((err) => {
                alert("error" + err)
            })
    }
    return (
        <div className="container">
            <h1>SignUp</h1>
            <form onSubmit={signup}>
                <input type="text" placeholder="fullname" id="fullname" />
                <br />
                <input type="text" placeholder="email" id="email" />
                <br />
                {/* <input type="text" placeholder="mobile" id="mobile" /> */}
                {/* <br /> */}
                {/* <input type="password" name="" id="password" placeholder="password" /> */}
                {/* <br /> */}
                <input type="password" name="" id="cpassword" placeholder="confirm password" />
                <br />
                <button type="submit">Sign Up</button>
            </form>

        </div>
    )
}
export default Register;
